mapper.select:
SELECT atc_no, atc_title, atc_content, atc_writer, atc_regdate, atc_hit, atc_replycnt
FROM tbl_article
WHERE atc_no = #{articleNo}

mapper.update:
UPDATE tbl_article
SET atc_title = #{title}, atc_content = #{content}
WHERE atc_no = #{articleNo}

UPDATE tbl_article
SET atc_replycnt = atc_replycnt + #{amount}
WHERE atc_no = #{articleNo}

UPDATE tbl_article
SET atc_hit = atc_hit + 1
WHERE atc_no = #{articleNo}
