mapper.update:
UPDATE tbl_reply
SET rpl_text = #{replyText}
WHERE rpl_no = #{replyNo}

mapper.select:
SELECT rpl_no, atc_no, rpl_writer, rpl_text, rpl_regdate
FROM tbl_reply
WHERE atc_no = #{articleNo}
ORDER BY rpl_no
LIMIT #{criteria.pageStart}, #{criteria.perPageNum}

SELECT COUNT(atc_no)
FROM tbl_reply
WHERE atc_no = #{articleNo}
